using namespace std;

/*
Return a list of all the keys in the .ini file.
*/
int main() {
    ifstream file("example.ini");
    string line;
    vector<string> keys;

    while (getline(file, line)) {
        stringstream ss(line);
        string key;
        while (ss >> key) {
            keys.push_back(key);
        }
    }

    file.close();

    cout << "Keys in .ini file: " << endl;
    for (string key : keys) {
        cout << key << endl;
    }

    return 0;
}// language: Python
