import json

with open('movies.json') as f:
    movies = json.load(f)

def get_movies_by_year(year):
    movies_by_year = {}
    for movie in movies:
        if movie['year'] == year:
            movies_by_year[movie['title']] = movie['summary']
    return movies_by_year

def get_movies_by_year_and_title(year, title):
    movies_by_year = get_movies_by_year(year)
    if title in movies_by_year:
        return movies_by_year[title]
    else:
        return None

def get_movies_by_year_and_title_and_summary(year, title, summary):
    movies_by_year = get_movies_by_year(year)
    if title in movies_by_year:
        if summary in movies_by_year[title]:
            return movies_by_year[title][summary]
        else:
            return None
    else:
        return None

